CREATE PROCEDURE [dbo].[SpUpdateProduct]   @ProductId int,   @ShopId int,   @BrandId int,   @ProductName nvarchar(50),   @ProductColor nvarchar(50),   @ImagePath nvarchar(MAX)  AS  BEGIN   UPDATE   Product   SET      shop_id = @ShopId,   brand_id = @BrandId,   product_name = @ProductName,   product_color = @ProductColor,   product_image = @ImagePath      WHERE product_id = @ProductId  END